import React, { Component } from "react";
import validator from "validator";
// MATERIAL UI
import {
  Grid,
  Input,
  InputLabel,
  FormControl,
  Typography,
  Button,
  Paper,
  InputAdornment,
  IconButton,
  withStyles,
  Snackbar,
  Fade,
  FormHelperText
} from "@material-ui/core";
import { Visibility, VisibilityOff } from "@material-ui/icons";

const styles = theme => ({
  container: {
    display: "flex",
    flexWrap: "wrap"
  },
  formControl: {
    margin: theme.spacing.unit,
    display: "flex"
  }
});

class AccountForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showPassword: false,
      userName: "",
      userEmail: "",
      userPhone: "",
      userPassword: "",
      inputConfirmPassword: "",
      displayPassInput: true, //verif
      showPasswordBis: false,
      displayConfirmPassInput: false, //verif
      errorMail: ""
    };
  }

  handleClickShowPassword(e) {
    this.setState(state => ({ showPassword: !state.showPassword }));
  }

  handleClickShowPasswordBis(e) {
    this.setState(state => ({ showPasswordBis: !state.showPasswordBis }));
  }

  handleChange(e) {
    this.setState({
      [e.target.id]: e.target.value,
      touched: true
    });
  }

  goToCreate() {
    this.props.history.push("/identification/creation", {
      url: this.props.prevPath
    });
  }

  render() {
    const {
      classes,
      formType,
      onSubmit,
      onBlurCheck,
      message,
      onOpenSnack,
      onSnackClose,
      title
    } = this.props;
    const {
      userName,
      userEmail,
      userPhone,
      userPassword,
      inputConfirmPassword
    } = this.state;

    let errorName = "";
    let errorEmail = "";
    let errorPhone = "";
    let errorMdp = "";
    let conferrorMdp = "";
    let condition = true;

    if (this.props.formType === "signin") {
      const regexPassword = new RegExp(
        "^(?=.*?[A-Z])(?=.*?[a-z])(?=.*?[0-9])(?=.*?[^ws]).{8,}$"
      );
      const regexEmail = new RegExp(
        "^([a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+.[a-zA-Z]{1,3})$"
      );

      // error name
      if (this.state.userName.length >= 1) {
        errorName =
          this.state.userName.length < 3 ? "votre nom est trop court" : "";
      }

      // error email
      if (this.state.userEmail.length >= 1) {
        errorEmail = regexEmail.test(this.state.userEmail)
          ? ""
          : "Votre email n'est pas valide";
      }

      // error phone

      if (this.state.userPhone.length >= 1) {
        errorPhone = validator.isMobilePhone(this.state.userPhone, "fr-FR")
          ? ""
          : "Votre numéro est incorrect";
      }

      // error password

      if (this.state.userPassword.length >= 1) {
        errorMdp = regexPassword.test(this.state.userPassword)
          ? ""
          : "votre mot de passe doit contenir 8 caractères dont un chiffre et une majuscule";
      }

      if (this.state.userPassword.length >= 1) {
        conferrorMdp =
          this.state.inputConfirmPassword === this.state.userPassword
            ? ""
            : "Vos mots de passe ne sont pas identiques";
      }

      if (
        !conferrorMdp.length &&
        !errorMdp.length &&
        !errorPhone.length &&
        !errorEmail.length &&
        !errorName.length
      ) {
        condition = false;
      }
    } else if (this.props.formType === "login") {
      if (this.state.userEmail.length < 1) {
        errorEmail = "Veuillez entrer votre adresse email";
      }
      if (this.state.userPassword.length < 1) {
        errorMdp = "Veuillez entrer votre mot de passe";
      }

      if (!errorMdp.length && !errorEmail.length) {
        condition = false;
      }
    }

    return (
      <div>
        <Grid container justify="center">
          <Grid item xs={10}>
            <Paper
              style={{
                padding: "1.6rem 1rem 2rem 1rem",
                marginTop: "3rem"
              }}
            >
              <form
                onChange={e => this.handleChange(e)}
                onBlur={onBlurCheck}
                onSubmit={e => {
                  e.preventDefault();
                  onSubmit({
                    userName: this.state.userName,
                    userEmail: this.state.userEmail,
                    userPhone: this.state.userPhone,
                    userPassword: this.state.userPassword,
                    inputConfirmPassword: this.state.inputConfirmPassword
                  });
                  if (this.props.formType === "signin") {
                    this.props.redirectTo();
                  }
                }}
              >
                <Typography variant="subheading" color="primary" align="center">
                  {title}
                </Typography>
                {/* NAME */}
                {formType === "signin" && (
                  <FormControl
                    error={errorName.length}
                    className={classes.formControl}
                  >
                    <InputLabel htmlFor="name">Nom</InputLabel>
                    <Input id="userName" name="name" type="text" />
                    {errorName !== "" ? (
                      <FormHelperText style={{ color: "red" }}>
                        {errorName}
                      </FormHelperText>
                    ) : null}
                  </FormControl>
                )}
                {/* EMAIL */}
                <FormControl
                  error={errorEmail.length}
                  className={classes.formControl}
                >
                  <InputLabel htmlFor="email">Email</InputLabel>
                  <Input id="userEmail" type="email" name="email" />
                  {errorEmail !== "" ? (
                    <FormHelperText style={{ color: "red" }}>
                      {errorEmail}
                    </FormHelperText>
                  ) : null}
                </FormControl>
                {/* TELEPHONE */}
                {formType === "signin" && (
                  <FormControl
                    error={errorPhone.length}
                    className={classes.formControl}
                  >
                    <InputLabel htmlFor="phone">Téléphone</InputLabel>
                    <Input id="userPhone" type="tel" name="phone" />
                    {errorPhone !== "" ? (
                      <div style={{ color: "red" }}>{errorPhone}</div>
                    ) : null}
                  </FormControl>
                )}
                {/* PASSWORD */}
                <FormControl
                  className={classes.formControl}
                  aria-describedby="password"
                  required
                  error={errorMdp.length}
                >
                  <InputLabel htmlFor="password">Password</InputLabel>

                  <Input
                    id="userPassword"
                    type={this.state.showPassword ? "text" : "password"}
                    name="userPassword"
                    onChange={e => this.handleChange(e)}
                    endAdornment={
                      <InputAdornment position="end">
                        <IconButton
                          aria-label="Toggle password visibility"
                          onClick={e => this.handleClickShowPassword(e)}
                        >
                          {this.state.showPassword ? (
                            <VisibilityOff />
                          ) : (
                            <Visibility />
                          )}
                        </IconButton>
                      </InputAdornment>
                    }
                  />
                  {errorMdp !== "" ? (
                    <FormHelperText style={{ color: "red" }}>
                      {errorMdp}
                    </FormHelperText>
                  ) : null}
                </FormControl>
                {/* PASSWORD CONFIRM */}
                {formType === "signin" && this.state.userPassword.length > 0 && (
                  <FormControl
                    className={classes.formControl}
                    aria-describedby="password"
                  >
                    <InputLabel htmlFor="inputConfirmPassword">
                      Confirmer Mot de passe
                    </InputLabel>

                    <Input
                      id="inputConfirmPassword"
                      error={conferrorMdp}
                      type={this.state.showPasswordBis ? "text" : "password"}
                      name="confirmPassword"
                      onChange={e => this.handleChange(e)}
                      endAdornment={
                        <InputAdornment position="end">
                          <IconButton
                            aria-label="Toggle password visibility"
                            onClick={e => this.handleClickShowPasswordBis(e)}
                          >
                            {this.state.showPasswordBis ? (
                              <VisibilityOff />
                            ) : (
                              <Visibility />
                            )}
                          </IconButton>
                        </InputAdornment>
                      }
                    />

                    {conferrorMdp !== "" ? (
                      <FormHelperText style={{ color: "red" }}>
                        {conferrorMdp}
                      </FormHelperText>
                    ) : null}
                  </FormControl>
                )}
                <Button
                  style={{ marginTop: "2rem" }}
                  variant="contained"
                  fullWidth={true}
                  color="secondary"
                  className={classes.button}
                  type="submit"
                  disabled={condition}
                >
                  Valider
                </Button>
                {/* Part for log in only */}

                {formType === "login" && (
                  <div>
                    <Typography
                      component="a"
                      href="/identification/rappel-de-mot-de-passe"
                      align="right"
                      variant="caption"
                      style={{ marginTop: "0.3rem", textDecoration: "none" }}
                    >
                      Mot de passe oublié
                    </Typography>
                    <Typography
                      variant="subheading"
                      color="primary"
                      align="center"
                      style={{ marginTop: "2.6rem", marginBottom: "0.7rem" }}
                    >
                      C'est votre première commande
                    </Typography>
                    <Button
                      variant="contained"
                      fullWidth={true}
                      className={classes.button}
                      onClick={() => this.goToCreate()}
                    >
                      Créer un compte
                    </Button>
                  </div>
                )}
              </form>
            </Paper>
          </Grid>
        </Grid>
        <Snackbar
          open={onOpenSnack}
          autoHideDuration={3000}
          onClose={onSnackClose}
          TransitionComponent={Fade}
          ContentProps={{
            "aria-describedby": "message-id"
          }}
          message={message}
        />
      </div>
    );
  }
}

export default withStyles(styles)(AccountForm);
